SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_grupos_auditor ( nr_seq_analise_p bigint, dt_referencia_p timestamp, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar a sequência dos grupos que o auditor está inserido na análise passada
como parâmetro. Serve para montar o WcPanel de seleção de grupo
As sequencias voltam separadas por espaço para fazer um like
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_grupos_analise_w		varchar(2000)	:= null;
nr_seq_grupo_w			bigint;

C01 CURSOR FOR
	SELECT	a.nr_seq_grupo
	from	pls_auditoria_conta_grupo a
	where	a.nr_seq_analise	= nr_seq_analise_p
	and	exists (SELECT	1
			from	pls_membro_grupo_aud x
			where	x.nr_seq_grupo	= a.nr_seq_grupo
			and	x.nm_usuario_exec = nm_usuario_p
			and	x.ie_situacao = 'A'
			and	x.dt_inclusao	>= coalesce(dt_referencia_p,x.dt_inclusao));


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_grupo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_grupos_analise_w	:= ds_grupos_analise_w || ' ' || nr_seq_grupo_w || ' ';
	end;
end loop;
close C01;

return ds_grupos_analise_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
