CREATE OR REPLACE FUNCTION pls_obter_grupo_analise_atual ( nr_seq_auditoria_p bigint) RETURNS bigint AS $body$
DECLARE


nr_retorno_w			bigint;
nr_seq_ordem_w			bigint;


BEGIN

begin
	select	coalesce(min(nr_seq_ordem),0)
	into STRICT	nr_seq_ordem_w
	from	pls_auditoria_grupo
	where	coalesce(dt_liberacao::text, '') = ''
	and	nr_seq_auditoria = nr_seq_auditoria_p;

	select	coalesce(nr_sequencia,0)
	into STRICT	nr_retorno_w
	from	pls_auditoria_grupo
	where	nr_seq_ordem		= nr_seq_ordem_w
	and	nr_seq_auditoria	= nr_seq_auditoria_p;
exception
when others then
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_retorno_w
	from	pls_auditoria_grupo
	where	nr_seq_ordem		= nr_seq_ordem_w
	and	nr_seq_auditoria	= nr_seq_auditoria_p
	and	coalesce(dt_liberacao::text, '') = '';
end;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
