SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_grupo_analise_conta (nr_seq_analise_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar o grupo atual de análise da conta - Grupo que está na vez no fluxo
da análise
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
/* IE_OPCAO_P
C - Codigo
D - Nome
*/
nr_seq_grupo_w		bigint	:= null;
nm_grupo_w		varchar(255)	:= null;
ds_retorno_w		varchar(255)	:= null;
nr_sequencia_w		bigint;
nr_seq_ordem_w		bigint;


BEGIN

if (nr_seq_analise_p IS NOT NULL AND nr_seq_analise_p::text <> '') then
	select	min(a.nr_seq_ordem)
	into STRICT	nr_seq_ordem_w
	from	pls_auditoria_conta_grupo a
	where	a.nr_seq_analise	= nr_seq_analise_p
	and	coalesce(a.dt_liberacao::text, '') = '';

	if (nr_seq_ordem_w IS NOT NULL AND nr_seq_ordem_w::text <> '') then
		select	min(a.nr_sequencia)
		into STRICT	nr_sequencia_w
		from	pls_auditoria_conta_grupo a
		where	a.nr_seq_analise	= nr_seq_analise_p
		and	a.nr_seq_ordem		= nr_seq_ordem_w
		and	coalesce(a.dt_liberacao::text, '') = '';

		if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
			select	a.nr_seq_grupo,
				b.nm_grupo_auditor
			into STRICT	nr_seq_grupo_w,
				nm_grupo_w
			from	pls_grupo_auditor b,
				pls_auditoria_conta_grupo a
			where	b.nr_sequencia	= a.nr_seq_grupo
			and	a.nr_sequencia	= nr_sequencia_w
			and	coalesce(a.dt_liberacao::text, '') = '';

			if (ie_opcao_p = 'D') then
				ds_retorno_w	:= nm_grupo_w;
			else
				ds_retorno_w	:= nr_seq_grupo_w;
			end if;
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_grupo_analise_conta (nr_seq_analise_p bigint, ie_opcao_p text) FROM PUBLIC;

