CREATE OR REPLACE FUNCTION pls_obter_grupo_ans_conta_pos ( nr_seq_conta_pos_p bigint) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: Verificar se a conta se encaixa nos filtros específicos de conta para 
as ocorrências combinadas de conta médica. 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ X] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
			 
ds_retorno_w		varchar(255);
		

BEGIN 
 
select	max(ds_grupo) 
into STRICT	ds_retorno_w 
from (	SELECT	c.ds_grupo ds_grupo 
		from	pls_conta_pos_estabelecido a, 
			pls_conta_proc b, 
			ans_grupo_despesa c 
		where	a.nr_seq_conta_proc 	= b.nr_sequencia 
		and	b.nr_seq_grupo_ans 	= c.nr_sequencia 
		and	a.nr_sequencia 		= nr_seq_conta_pos_p 
		
union all
 
		SELECT	c.ds_grupo ds_grupo 
		from	pls_conta_pos_estabelecido a, 
			pls_conta_mat b, 
			ans_grupo_despesa c 
		where	a.nr_seq_conta_mat 	= b.nr_sequencia 
		and	b.nr_seq_grupo_ans	= c.nr_sequencia 
		and	a.nr_sequencia 		= nr_seq_conta_pos_p) alias1;	
 
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
