SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_grupo_contrato ( nr_seq_segurado_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
	GR - Grupo de relacionamentos
	GC - Grupo de criterio de pre√ßos
*/
ds_retorno_w			varchar(255);


BEGIN
if (ie_opcao_p	= 'GR') then
	select	max(c.ds_grupo)
	into STRICT	ds_retorno_w
	from	pls_contrato 	a,
		pls_contrato_grupo	b,
		pls_grupo_contrato	c
	where	a.nr_sequencia	= b.nr_seq_contrato
	and	c.nr_sequencia	= b.nr_seq_grupo
	and	exists (	SELECT	1
			from	pls_segurado x
			where	x.nr_seq_contrato	= a.nr_sequencia
			and	x.nr_sequencia		= nr_seq_segurado_p );

elsif (ie_opcao_p	= 'GC')then

	select	max(b.ds_grupo)
	into STRICT	ds_retorno_w
	from	pls_contrato		a,
		pls_preco_grupo_contrato	b,
		pls_preco_contrato		c
	where	c.nr_seq_grupo	= b.nr_sequencia
	and	c.nr_seq_contrato	= a.nr_sequencia
	and	exists (	SELECT	1
			from	pls_segurado x
			where	x.nr_seq_contrato	= a.nr_sequencia
			and	x.nr_sequencia		= nr_seq_segurado_p);
end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
