CREATE OR REPLACE FUNCTION pls_obter_grupo_pendente ( nr_seq_analise_p bigint ) RETURNS bigint AS $body$
DECLARE


nr_retorno_w	bigint;

/*OS 339634 */

BEGIN

select 	max(nr_seq_grupo)
into STRICT	nr_retorno_w
from	pls_auditoria_conta_grupo
where	nr_seq_analise	= nr_seq_analise_p
and	coalesce(dt_liberacao::text, '') = ''
and	nr_seq_ordem	= (	SELECT	min(nr_seq_ordem)
				from	pls_auditoria_conta_grupo
				where	nr_seq_analise = nr_seq_analise_p
				and	coalesce(dt_liberacao::text, '') = ''	);

return	nr_retorno_w;

end;
