SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_grupo_pertence_ocorr ( nr_seq_ocorrencia_p bigint, nr_seq_grupo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permissao_w			varchar(1);
qt_grupo_w			integer;
ds_retorno_w			varchar(1) := 'N';
qt_ocorrencia_informativa_w	integer;


BEGIN

if (coalesce(nr_seq_ocorrencia_p,0) <> 0) then
	select	max(ie_permissao)
	into STRICT	ie_permissao_w
	from	pls_grupo_auditor
	where	nr_sequencia = nr_seq_grupo_p;

	if (ie_permissao_w in ('T','R')) then
		ds_retorno_w := 'S';
	end if;

	if (ds_retorno_w <> 'S') then
		select	count(1)
		into STRICT	qt_ocorrencia_informativa_w
		from	pls_ocorrencia
		where	nr_sequencia	= nr_seq_ocorrencia_p
		and	ie_auditoria	= 'N'
		and	ie_situacao	= 'A';

		if (qt_ocorrencia_informativa_w = 0) then
			select  count(1)
			into STRICT	qt_grupo_w
			from 	pls_ocorrencia_grupo
			where 	nr_seq_ocorrencia 	= nr_seq_ocorrencia_p
			and	nr_seq_grupo		= nr_seq_grupo_p
			and	ie_situacao = 'A';

			if (qt_grupo_w > 0) then
				ds_retorno_w := 'S';
			end if;
		else
			ds_retorno_w := 'S';
		end if;
	end if;
else
	ds_retorno_w := 'S';
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
