CREATE OR REPLACE FUNCTION pls_obter_grupo_regra_mens ( nr_seq_regra_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


/*
	N = Nome do grupo de regras
*/
ds_retorno_w		varchar(255);
ds_regra_w		varchar(255);


BEGIN

select	ds_regra
into STRICT	ds_regra_w
from	pls_mensalidade_grupo
where	nr_sequencia	= nr_seq_regra_p;

if (ie_tipo_p	= 'N') then
	ds_retorno_w	:= ds_regra_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
