SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_guia_aut_principal ( nr_seq_guia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_guia_w			bigint	:= 0;
cd_guia_principal_w		varchar(20);
nr_seq_segurado_w		bigint;


BEGIN

/* Verificar se a guia de autorização possui uma guia principal */

select	cd_guia_principal,
	nr_seq_segurado
into STRICT	cd_guia_principal_w,
	nr_seq_segurado_w
from	pls_guia_plano
where	nr_sequencia	= nr_seq_guia_p;

if (coalesce(cd_guia_principal_w,'X') <> 'X') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_guia_w
	from	pls_guia_plano
	where	cd_guia		= cd_guia_principal_w
	and	nr_seq_segurado	= nr_seq_segurado_w;
end if;

return	nr_seq_guia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_guia_aut_principal ( nr_seq_guia_p bigint) FROM PUBLIC;

