CREATE OR REPLACE FUNCTION pls_obter_guia_medico ( CD_PESSOA_FISICA_P text, CD_CGC_P text, NR_SEQ_PRESTADOR_P text, IE_GUIA_P text, CD_IE_GUIA_P text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);
ie_guia_medico_w	varchar(20);


BEGIN
ds_retorno_w		:= '';
ie_guia_medico_w	:= '';

if (CD_CGC_P IS NOT NULL AND CD_CGC_P::text <> '') then
	begin
	select 	b.ie_guia_medico
	into STRICT 	ie_guia_medico_w
	from 	ptu_prestador a,
			pls_cooperado_ausencia b,
			pls_cooperado c
	where 	b.nr_seq_cooperado 	= c.nr_sequencia
	and		a.cd_cgc 			= c.cd_cgc
	and		CD_CGC_P			= a.cd_cgc
	and		NR_SEQ_PRESTADOR_P  = a.nr_sequencia;
	end;
elsif (CD_PESSOA_FISICA_P IS NOT NULL AND CD_PESSOA_FISICA_P::text <> '') then
	begin
	select 	b.ie_guia_medico
	into STRICT 	ie_guia_medico_w
	from 	ptu_prestador a,
			pls_cooperado_ausencia b,
			pls_cooperado c
	where 	b.nr_seq_cooperado 	= c.nr_sequencia
	and		a.cd_pessoa_fisica 	= c.cd_pessoa_fisica
	and		CD_PESSOA_FISICA_P	= a.cd_pessoa_fisica
	and		NR_SEQ_PRESTADOR_P  = a.nr_sequencia;
	end;
end if;

if (ie_guia_medico_w = 'S') then
	if (IE_GUIA_P = 'IE_GUIA_ESPEC' AND CD_IE_GUIA_P = '1') then
		begin
		select 	lpad(substr(obter_ptu_especialidade_rce(max(a.nr_sequencia), 1,'IE_GUIA_ESPEC'),1,1),1,'N')
		into STRICT 	ds_retorno_w
		from 	ptu_prestador a,
				pls_cooperado_ausencia b,
				pls_cooperado c
		where 	b.nr_seq_cooperado 	= c.nr_sequencia
		and (a.cd_pessoa_fisica 	= c.cd_pessoa_fisica or
				 a.cd_cgc 			= c.cd_cgc)
		and (CD_PESSOA_FISICA_P	= a.cd_pessoa_fisica or
				 CD_CGC_P			= a.cd_cgc)
		and		NR_SEQ_PRESTADOR_P  = a.nr_sequencia;
		end;
	elsif (IE_GUIA_P = 'IE_GUIA_ESPEC' AND CD_IE_GUIA_P = '2') then
		begin
		select  lpad(substr(obter_ptu_especialidade_rce(a.nr_sequencia, 2,'IE_GUIA_ESPEC'),1,1),1,'N')
		into STRICT 	ds_retorno_w
		from 	ptu_prestador a,
				pls_cooperado_ausencia b,
				pls_cooperado c
		where 	b.nr_seq_cooperado 	= c.nr_sequencia
		and (a.cd_pessoa_fisica 	= c.cd_pessoa_fisica or
				 a.cd_cgc 			= c.cd_cgc)
		and (CD_PESSOA_FISICA_P	= a.cd_pessoa_fisica or
				 CD_CGC_P			= a.cd_cgc)
		and		NR_SEQ_PRESTADOR_P  = a.nr_sequencia;
		end;
	elsif (IE_GUIA_P = 'IE_GUIA_ATUA' AND CD_IE_GUIA_P = '1') then
		begin
		select  lpad(substr(obter_ptu_especialidade_rce(a.nr_sequencia, 1,'IE_GUIA_ATUA'),1,1),1,' ')
		into STRICT 	ds_retorno_w
		from 	ptu_prestador a,
				pls_cooperado_ausencia b,
				pls_cooperado c
		where 	b.nr_seq_cooperado 	= c.nr_sequencia
		and (a.cd_pessoa_fisica 	= c.cd_pessoa_fisica or
				 a.cd_cgc 			= c.cd_cgc)
		and (CD_PESSOA_FISICA_P	= a.cd_pessoa_fisica or
				 CD_CGC_P			= a.cd_cgc)
		and		NR_SEQ_PRESTADOR_P  = a.nr_sequencia;
		end;
	elsif (IE_GUIA_P = 'IE_GUIA_ATUA' AND CD_IE_GUIA_P = '2') then
		begin
		select  lpad(substr(obter_ptu_especialidade_rce(a.nr_sequencia, 2,'IE_GUIA_ATUA'),1,1),1,' ')
		into STRICT 	ds_retorno_w
		from 	ptu_prestador a,
				pls_cooperado_ausencia b,
				pls_cooperado c
		where 	b.nr_seq_cooperado 	= c.nr_sequencia
		and (a.cd_pessoa_fisica 	= c.cd_pessoa_fisica or
				 a.cd_cgc 			= c.cd_cgc)
		and (CD_PESSOA_FISICA_P	= a.cd_pessoa_fisica or
				 CD_CGC_P			= a.cd_cgc)
		and		NR_SEQ_PRESTADOR_P  = a.nr_sequencia;
		end;
	end if;
elsif (ie_guia_medico_w = 'N') then
	ds_retorno_w := 'N';
end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
