CREATE OR REPLACE FUNCTION pls_obter_guia_principal ( nr_seq_guia_p pls_guia_plano.nr_sequencia%type, nr_seq_requisicao_p pls_requisicao.nr_sequencia%type, nr_seq_execucao_p pls_execucao_requisicao.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


cd_guia_principal_w			pls_guia_plano.cd_guia_principal%type	:= '';


BEGIN
if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	select	coalesce(cd_guia_principal, nr_seq_guia_principal)
	into STRICT	cd_guia_principal_w
	from	pls_guia_plano
	where	nr_sequencia = nr_seq_guia_p;
elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	select	coalesce(cd_guia_principal, nr_seq_guia_principal)
	into STRICT	cd_guia_principal_w
	from	pls_requisicao
	where	nr_sequencia = nr_seq_requisicao_p;
elsif (nr_seq_execucao_p IS NOT NULL AND nr_seq_execucao_p::text <> '') then
	select	coalesce(b.cd_guia_principal, b.nr_seq_guia_principal)
	into STRICT	cd_guia_principal_w
	from	pls_execucao_requisicao a,
		pls_requisicao b
	where	a.nr_seq_requisicao = b.nr_sequencia
	and	a.nr_sequencia = nr_seq_execucao_p;
end if;

return	cd_guia_principal_w;

end;
