CREATE OR REPLACE FUNCTION pls_obter_guia_prorrogacao_web ( cd_guia_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
nr_seq_guia_w		bigint;
ie_tipo_guia_w 		varchar(2);
ie_status_w 		varchar(2);



BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_guia_w
from	pls_guia_plano
where	cd_guia			= cd_guia_p
and	cd_estabelecimento 	= cd_estabelecimento_p;

if (nr_seq_guia_w IS NOT NULL AND nr_seq_guia_w::text <> '') then
	select	ie_tipo_guia,
		ie_status
	into STRICT	ie_tipo_guia_w,
		ie_status_w
	from	pls_guia_plano
	where	nr_sequencia = nr_seq_guia_w;

	if (coalesce(ie_tipo_guia_w,'2') = '1' and coalesce(ie_status_w,'2') = '1') then
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
