SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_hist_plano_benef ( nr_seq_segurado_p pls_segurado.nr_sequencia%type, dt_referencia timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* 	ie_opcao:
	PR - Plano vigente na data de referencia
*/
ds_retorno_w	varchar(255);


BEGIN
ds_retorno_w := '';

-- PR - Plano vigente na data de referencia
if	(ie_opcao_p = 'PR' AND dt_referencia IS NOT NULL AND dt_referencia::text <> '') then
	begin
		select	to_char(nr_seq_plano_atual)
		into STRICT	ds_retorno_w
		from	pls_segurado_alt_plano
		where	nr_sequencia	= (	SELECT	max(x.nr_sequencia)
						from	pls_segurado_alt_plano	x
						where	nr_seq_segurado	= nr_seq_segurado_p
						and	x.ie_situacao = 'A'
						and	trunc(dt_alteracao, 'dd') < trunc(dt_referencia, 'dd'));
	exception
		when no_data_found then ds_retorno_w := '';
	end;
	
	if (coalesce(ds_retorno_w::text, '') = '') then
		select	max(nr_seq_plano)
		into STRICT	ds_retorno_w
		from	pls_segurado
		where	nr_sequencia	= nr_seq_segurado_p;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
