CREATE OR REPLACE FUNCTION pls_obter_idade_segurado ( nr_seq_segurado_p bigint, dt_atual_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter a idade do segurado
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++

	IE_OPCAO_P
	E - Faixa etária
	S - Anos/Meses/Dias
	AM - Anos/Meses
	A - Anos
	D - Anos descritivo
	M -  Meses da idade total
	MM - Meses no ano
	DI - Dias no mês
	DIA - Dias da idade total
	AMD -  Anos/Meses/Dias
*/dt_nascimento_w			timestamp;


BEGIN

if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then
	select	max(dt_nascimento)
	into STRICT	dt_nascimento_w
	from	pessoa_fisica	b,
		pls_segurado	a
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
	and	a.nr_sequencia		= nr_seq_segurado_p;

	return obter_idade(dt_nascimento_w, dt_atual_p, ie_opcao_p);
else
	return null;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
