CREATE OR REPLACE FUNCTION pls_obter_id_regra_ident_filho (nr_seq_segurado_p pls_segurado.nr_sequencia%type) RETURNS PTU_REGRA_ID_FILHO.ID_FILHO%TYPE AS $body$
DECLARE


nr_seq_parentesco_w		pls_segurado.nr_seq_parentesco%type;
nr_seq_regra_id_filho_w		ptu_regra_id_filho.nr_sequencia%type;
id_filho_w			ptu_regra_id_filho.id_filho%type;
qt_idade_w			integer;


BEGIN

	select	obter_idade(a.dt_nascimento, clock_timestamp(), 'A'),
		b.nr_seq_parentesco
	into STRICT	qt_idade_w,
		nr_seq_parentesco_w
	from	pessoa_fisica	a,
		pls_segurado	b
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
	and	b.nr_sequencia		= nr_seq_segurado_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_id_filho_w
	from	ptu_regra_id_filho
	where (nr_seq_parentesco_w	= nr_seq_parentesco or coalesce(nr_seq_parentesco::text, '') = '')
	and (qt_idade_w 		>= qt_idade_inicial or coalesce(qt_idade_inicial::text, '') = '')
	and (qt_idade_w		<= qt_idade_final or coalesce(qt_idade_final::text, '') = '');

	if (nr_seq_regra_id_filho_w IS NOT NULL AND nr_seq_regra_id_filho_w::text <> '') then
		select	id_filho
		into STRICT	id_filho_w
		from	ptu_regra_id_filho
		where	nr_sequencia	= nr_seq_regra_id_filho_w;

		return id_filho_w;
	else
		return null;
	end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
