CREATE OR REPLACE FUNCTION pls_obter_ie_preco_conta (nr_seq_conta_p pls_conta.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(255);


BEGIN

select 	max(d.ie_preco)
into STRICT	ds_retorno_w
from 	pls_conta    b,
	pls_segurado c,
	pls_plano    d
where 	b.nr_sequencia    = nr_seq_conta_p
and     b.nr_seq_segurado = c.nr_sequencia
and   	c.nr_seq_plano    = d.nr_sequencia;

return	coalesce(ds_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
