SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_ie_valor_glosa ( nr_seq_item_p bigint, ie_tipo_item_p text ) RETURNS bigint AS $body$
DECLARE


nr_retorno_w		bigint;
nr_seq_regra_glosa_w	bigint;

--1 -  Apresentado
--2 -  Calculado
BEGIN

if (ie_tipo_item_p in ('P', 'R')) then

	select	max(nr_seq_regra_glosa)
	into STRICT	nr_seq_regra_glosa_w
	from	pls_conta_proc
	where	nr_sequencia = nr_seq_item_p;

elsif (ie_tipo_item_p = 'M') then

	select	max(nr_seq_regra_glosa)
	into STRICT	nr_seq_regra_glosa_w
	from	pls_conta_mat
	where	nr_sequencia = nr_seq_item_p;

end if;

select	coalesce(max(ie_regra_valor),1)
into STRICT	nr_retorno_w
from	pls_regra_valor_glosa
where	nr_sequencia  = nr_seq_regra_glosa_w;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
