CREATE OR REPLACE FUNCTION pls_obter_indece_reajuste ( nr_seq_contrato_p bigint, ie_tipo_contrato_p text) RETURNS varchar AS $body$
DECLARE


IE_INDICE_REAJUSTE_w			varchar(10);


BEGIN

if (ie_tipo_contrato_p = 'PF') then
	select	max(z.IE_INDICE_REAJUSTE)
	into STRICT	IE_INDICE_REAJUSTE_w
	from	pls_reajuste		z,
		pls_reajuste_tabela	x
	where	x.nr_seq_reajuste	= z.nr_sequencia
	and	x.nr_seq_contrato	= nr_seq_contrato_p
	and	z.ie_status		= '2';
elsif (ie_tipo_contrato_p = 'PJ') then
	select	max(z.IE_INDICE_REAJUSTE)
	into STRICT	IE_INDICE_REAJUSTE_w
	from	pls_reajuste	z
	where	z.nr_seq_contrato	= nr_seq_contrato_p
	and	z.ie_status		= '2'
	and	coalesce(z.nr_seq_lote_deflator::text, '') = ''
	and	coalesce(z.tx_deflator::text, '') = '';
end if;

return	IE_INDICE_REAJUSTE_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
