CREATE OR REPLACE FUNCTION pls_obter_info_guia_maps_web ( ds_endereco_p text) RETURNS varchar AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
 
ds_info_google_maps_w	varchar(4000) 	:= null;
nm_prestador_w		varchar(255)	:= null;

C01 CURSOR FOR 
	SELECT	substr(obter_nome_pf_pj(a.cd_pessoa_fisica, a.cd_cgc),0,15) nm_prestador 
	from	pls_prestador a 
	where	a.ie_guia_medico = 'S' 
	and	(a.ie_tipo_endereco IS NOT NULL AND a.ie_tipo_endereco::text <> '') 
	and	pls_obter_end_prestador(a.nr_sequencia,null, null) = ds_endereco_p;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nm_prestador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
begin 
	if (coalesce(ds_info_google_maps_w::text, '') = '' ) then 
		ds_info_google_maps_w := nm_prestador_w;
	else 
		ds_info_google_maps_w := ds_info_google_maps_w ||'/'||nm_prestador_w;
	end if;
end;
end loop;
close C01;
 
ds_info_google_maps_w := ds_info_google_maps_w||' '|| ds_endereco_p;
 
return	ds_info_google_maps_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_info_guia_maps_web ( ds_endereco_p text) FROM PUBLIC;

