CREATE OR REPLACE FUNCTION pls_obter_info_perfil_web ( nr_seq_perfil_p pls_perfil_web.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nm_perfil_web_w		varchar(255) := '';

/*	ie_opcao_p
	'N' -	Nome do perfil
*/
BEGIN

if (ie_opcao_p = 'N') then

	select	max(substr(nm_perfil_web,1,255))
	into STRICT	nm_perfil_web_w
	from	pls_perfil_web
	where	nr_sequencia = nr_seq_perfil_p;
end if;

return	nm_perfil_web_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
