CREATE OR REPLACE FUNCTION pls_obter_inf_ate_regra_tempo ( qt_tempo_p bigint, ie_informacao_p text) RETURNS varchar AS $body$
DECLARE


ds_informacao_w		varchar(100);

ds_regra_w		varchar(100);
ds_cor_w		varchar(15);
qt_tempo_maximo_w	bigint;


BEGIN
if (qt_tempo_p IS NOT NULL AND qt_tempo_p::text <> '') then
	begin
	SELECT * FROM pls_obter_atend_regra_tempo(qt_tempo_p, ds_regra_w, ds_cor_w, qt_tempo_maximo_w) INTO STRICT ds_regra_w, ds_cor_w, qt_tempo_maximo_w;

	if (ie_informacao_p = 'R') then
		begin
		ds_informacao_w := ds_regra_w;
		end;
	elsif (ie_informacao_p = 'C') then
		begin
		ds_informacao_w := ds_cor_w;
		end;
	elsif (ie_informacao_p = 'T') then
		begin
		ds_informacao_w := qt_tempo_maximo_w;
		end;
	end if;
	end;
end if;
return ds_informacao_w;
end;
