CREATE OR REPLACE FUNCTION pls_obter_inf_pagto_novo ( nr_seq_prestador_p bigint, nr_seq_lote_p pls_lote_pagamento.nr_sequencia%type, cd_tributo_p tributo.cd_tributo%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(30);
cpf_cgc_w			varchar(255);
vl_total_base_calculo_w		pls_pp_valor_trib_pessoa.vl_base_calculo%type;
dt_vencimento_w			pls_pp_prestador.dt_venc_titulo%type;


BEGIN

cpf_cgc_w	:= '';

select	CASE WHEN coalesce(cd_cgc::text, '') = '' THEN  cd_pessoa_fisica  ELSE cd_cgc END
into STRICT	cpf_cgc_w
from	pls_prestador
where	nr_sequencia = nr_seq_prestador_p;

select	max(trunc(a.dt_venc_titulo,'mm'))
into STRICT	dt_vencimento_w
from   	pls_pp_prestador a
where	a.nr_seq_lote 		= nr_seq_lote_p
and	a.nr_seq_prestador 	= nr_seq_prestador_p;

select	coalesce(sum(coalesce(e.vl_base_calculo,0)),0)
into STRICT	vl_total_base_calculo_w
from	pls_pp_lote 			a,
	pls_pp_prestador		b,
	pls_prestador 			c,
	titulo_pagar 			d,
	pls_pp_valor_trib_pessoa	e
where	a.nr_sequencia 		= b.nr_seq_lote
and	b.nr_seq_prestador 	= c.nr_sequencia
and 	e.nr_seq_prestador 	= b.nr_seq_prestador
and	b.nr_titulo_pagar	= d.nr_titulo
and	((c.cd_pessoa_fisica		= cpf_cgc_w) or (c.cd_cgc			= cpf_cgc_w))	
and	e.cd_tributo			= cd_tributo_p
and	trunc(b.dt_venc_titulo,'mm')	= dt_vencimento_w
and	a.nr_sequencia <= nr_seq_lote_p;


if (ie_opcao_p = 'TB') then
	ds_retorno_w := substr(vl_total_base_calculo_w,1,30);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
