CREATE OR REPLACE FUNCTION pls_obter_intercam_cod_congen ( cd_congenere_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_intercambio_w		varchar(1)	:= 'A';
sg_estado_operadora_w		pessoa_juridica.sg_estado%type	:= null;
sg_estado_congenere_w		pessoa_juridica.sg_estado%type	:= null;
nr_seq_congenere_w		bigint;


BEGIN
select	max(nr_sequencia)
into STRICT	nr_seq_congenere_w
from	pls_congenere
where	cd_cooperativa = lpad(cd_congenere_p,4,'0');

begin
select	b.sg_estado
into STRICT	sg_estado_operadora_w
from	pessoa_juridica	b,
	pls_outorgante	a
where	a.cd_cgc_outorgante	= b.cd_cgc
and	a.cd_estabelecimento	= cd_estabelecimento_p;
exception
when others then
	sg_estado_operadora_w	:= null;
end;

begin
select	b.sg_estado
into STRICT	sg_estado_congenere_w
from	pessoa_juridica	b,
	pls_congenere	a
where	a.cd_cgc	= b.cd_cgc
and	a.nr_sequencia	= nr_seq_congenere_w;
exception
when others then
	sg_estado_congenere_w	:= null;
end;

if (sg_estado_congenere_w IS NOT NULL AND sg_estado_congenere_w::text <> '') and (sg_estado_operadora_w IS NOT NULL AND sg_estado_operadora_w::text <> '') then
	if (sg_estado_congenere_w = sg_estado_operadora_w) then
		ie_tipo_intercambio_w	:= 'E';
	else
		ie_tipo_intercambio_w	:= 'N';
	end if;
else
	ie_tipo_intercambio_w	:= 'A';
end if;

return ie_tipo_intercambio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
