CREATE OR REPLACE FUNCTION pls_obter_interface_cartao ( ie_tipo_segurado_p text, ie_opcao_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

/* IE_OPCAO_P 
	C = Código 
	D = Descrição Interface 
*/
 
 
ds_retorno_w			varchar(255);
nr_seq_emissor_provisorio_w	bigint;
nr_seq_emissor_w		bigint;


BEGIN 
 
select	coalesce(nr_seq_emissor_provisorio,nr_seq_emissor), 
	nr_seq_emissor 
into STRICT	nr_seq_emissor_provisorio_w, 
	nr_seq_emissor_w 
from	pls_parametros 
where	cd_estabelecimento	= cd_estabelecimento_p;
	 
if (ie_tipo_segurado_p	= 'R') then 
	select	cd_interface_envio 
	into STRICT	ds_retorno_w 
	from	pls_emissor_carteira 
	where	nr_sequencia	= nr_seq_emissor_provisorio_w;
else 
	select	cd_interface_envio 
	into STRICT	ds_retorno_w 
	from	pls_emissor_carteira 
	where	nr_sequencia	= nr_seq_emissor_w;
end if;
 
if (ie_opcao_p = 'D') then 
	select	ds_interface 
	into STRICT	ds_retorno_w 
	from	interface 
	where	cd_interface	= ds_retorno_w;	
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
