SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_internado_guia ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1);
ie_tipo_atend_tiss_w		bigint;
ie_tipo_guia_w			smallint;
nr_seq_guia_w			bigint;
nr_seq_conta_w			bigint;
nr_seq_tipo_atendimento_w	pls_tipo_atendimento.nr_sequencia%type;
nr_seq_requisicao_w		pls_requisicao.nr_sequencia%type;
ie_regime_atendimento_w		pls_conta.ie_regime_atendimento%type;

/*	ie_opcao_p
	A - Atendimento
	AP - Atendimento procedimento
	AM - Atendimento material
	C - Conta
	CP - Conta procedimento
	CM - Conta material
	R - Requisicao
	RP - Requisicao procedimento
	RM - Requisicao material
*/
BEGIN
if (ie_opcao_p	= 'A') then
	nr_seq_guia_w	:= nr_sequencia_p;
elsif (ie_opcao_p	= 'AP') then
	select	max(nr_seq_guia)
	into STRICT	nr_seq_guia_w
	from	pls_guia_plano_proc
	where	nr_sequencia	= nr_sequencia_p;
elsif (ie_opcao_p	= 'AM') then
	select	max(nr_seq_guia)
	into STRICT	nr_seq_guia_w
	from	pls_guia_plano_mat
	where	nr_sequencia	= nr_sequencia_p;
elsif (ie_opcao_p	= 'RP') then
	select	max(nr_seq_requisicao)
	into STRICT	nr_seq_requisicao_w
	from	pls_requisicao_proc
	where	nr_sequencia	= nr_sequencia_p;

	select	CASE WHEN a.ie_tipo_guia=1 THEN  'I' WHEN a.ie_tipo_guia=8 THEN  'I' WHEN a.ie_tipo_guia=2 THEN  'I' WHEN a.ie_tipo_guia=3 THEN  'A' END ,
		a.ie_tipo_guia,
		a.ie_tipo_atendimento,
		lpad(coalesce(a.ie_regime_atendimento, '1'), 2, '0')
	into STRICT	ie_retorno_w,
		ie_tipo_guia_w,
		ie_tipo_atend_tiss_w,
		ie_regime_atendimento_w
	from	pls_requisicao a
	where	a.nr_sequencia = nr_seq_requisicao_w;
	
	if (ie_tipo_guia_w	= 2) and (coalesce(ie_tipo_atend_tiss_w, 0) <> 7) and coalesce(ie_regime_atendimento_w,9) <> 3 then
		ie_retorno_w	:= 'A';
	end if;
elsif (ie_opcao_p	= 'RM') then
	select	max(nr_seq_requisicao)
	into STRICT	nr_seq_requisicao_w
	from	pls_requisicao_mat
	where	nr_sequencia	= nr_sequencia_p;

	select	CASE WHEN a.ie_tipo_guia=1 THEN  'I' WHEN a.ie_tipo_guia=8 THEN  'I' WHEN a.ie_tipo_guia=2 THEN  'I' WHEN a.ie_tipo_guia=3 THEN  'A' END ,
		a.ie_tipo_guia,
		a.ie_tipo_atendimento,
		lpad(coalesce(a.ie_regime_atendimento, '1'), 2, '0')
	into STRICT	ie_retorno_w,
		ie_tipo_guia_w,
		ie_tipo_atend_tiss_w,
		ie_regime_atendimento_w
	from	pls_requisicao a
	where	a.nr_sequencia = nr_seq_requisicao_w;
	
	if (ie_tipo_guia_w	= 2) and (coalesce(ie_tipo_atend_tiss_w, 0) <> 7) and	coalesce(ie_regime_atendimento_w,9) <> 3 then
		ie_retorno_w	:= 'A';
	end if;
end if;

if (ie_opcao_p	= 'C') then
	nr_seq_conta_w	:= nr_sequencia_p;
elsif (ie_opcao_p	= 'CP') then
	select	max(nr_seq_conta)
	into STRICT	nr_seq_conta_w
	from	pls_conta_proc
	where	nr_sequencia	= nr_sequencia_p;
elsif (ie_opcao_p	= 'CM') then
	select	max(nr_seq_conta)
	into STRICT	nr_seq_conta_w
	from	pls_conta_mat
	where	nr_sequencia	= nr_sequencia_p;
end if;

if (substr(ie_opcao_p,1,1)	= 'A') then
	select	CASE WHEN ie_tipo_guia=1 THEN 'I' WHEN ie_tipo_guia=8 THEN 'I' WHEN ie_tipo_guia=2 THEN 'I' END ,
		ie_tipo_guia,
		ie_tipo_atend_tiss
	into STRICT	ie_retorno_w,
		ie_tipo_guia_w,
		ie_tipo_atend_tiss_w
	from	pls_guia_plano
	where	nr_sequencia	= nr_seq_guia_w;

	if (ie_tipo_guia_w	= 2) and (coalesce(ie_tipo_atend_tiss_w,0)	<> 7 and coalesce(ie_regime_atendimento_w,9) <> 3) then
		ie_retorno_w	:= 'A';
	elsif (ie_tipo_guia_w	= 3) then
		ie_retorno_w	:= 'A';
	end if;
elsif (substr(ie_opcao_p,1,1)	= 'C') then
	select	CASE WHEN ie_tipo_guia=6 THEN 'I' WHEN ie_tipo_guia=5 THEN 'I' WHEN ie_tipo_guia=4 THEN 'I' END ,
		ie_tipo_guia,
		nr_seq_tipo_atendimento
	into STRICT	ie_retorno_w,
		ie_tipo_guia_w,
		nr_seq_tipo_atendimento_w
	from	pls_conta
	where	nr_sequencia	= nr_seq_conta_w;
	
	select	max(cd_tiss)
	into STRICT	ie_tipo_atend_tiss_w
	from	pls_tipo_atendimento
	where	nr_sequencia	= nr_seq_tipo_atendimento_w;
	
	if (coalesce(ie_tipo_atend_tiss_w::text, '') = '') then
		select	ie_tipo_atendimento_imp
		into STRICT	ie_tipo_atend_tiss_w
		from	pls_conta
		where	nr_sequencia = nr_seq_conta_w;
	end if;
	
	if (ie_tipo_guia_w	= 4) and (coalesce(ie_tipo_atend_tiss_w,0) <> 7 and coalesce(ie_regime_atendimento_w,9) <> 3) then
		ie_retorno_w	:= 'A';
	elsif (ie_tipo_guia_w	= 3) then
		ie_retorno_w	:= 'A';
	end if;
	
elsif (ie_opcao_p = 'R') then
	select	CASE WHEN a.ie_tipo_guia=1 THEN  'I' WHEN a.ie_tipo_guia=8 THEN  'I' WHEN a.ie_tipo_guia=2 THEN  'I' WHEN a.ie_tipo_guia=3 THEN  'A' END ,
		a.ie_tipo_guia,
		a.ie_tipo_atendimento,
		lpad(coalesce(a.ie_regime_atendimento, '1'), 2, '0')
	into STRICT	ie_retorno_w,
		ie_tipo_guia_w,
		ie_tipo_atend_tiss_w,
		ie_regime_atendimento_w
	from	pls_requisicao a
	where	a.nr_sequencia = nr_sequencia_p;
	
	if (ie_tipo_guia_w	= 2) and (coalesce(ie_tipo_atend_tiss_w, 0) <> 7) and	coalesce(ie_regime_atendimento_w,9) <> 3 then
		ie_retorno_w	:= 'A';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_internado_guia ( nr_sequencia_p bigint, ie_opcao_p text) FROM PUBLIC;

