CREATE OR REPLACE FUNCTION pls_obter_inter_ret_desc_emp ( nr_seq_empresa_p bigint) RETURNS varchar AS $body$
DECLARE


cd_interface_w	integer;
ds_interface_w	varchar(255);


BEGIN
if (nr_seq_empresa_p IS NOT NULL AND nr_seq_empresa_p::text <> '') then
	begin
	select	coalesce(max(cd_interface_retorno),0)
	into STRICT	cd_interface_w
	from	pls_desc_empresa
	where	nr_sequencia = nr_seq_empresa_p;

	select 	max(ds_interface)
	into STRICT	ds_interface_w
	from 	interface
	where 	cd_interface = cd_interface_w;
	exception
		when others then
		ds_interface_w:= '';
	end;
end if;
return ds_interface_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
