CREATE OR REPLACE FUNCTION pls_obter_item_lib_segurado ( cd_perfil_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
qt_registros_w		bigint;


BEGIN

ds_retorno_w	:= 'N';

select	count(*)
into STRICT	qt_registros_w
from	pls_item_contr_benef_lib
where	cd_perfil	= cd_perfil_p
and	nr_seq_item	= nr_seq_item_p;

if (qt_registros_w > 0) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
