CREATE OR REPLACE FUNCTION pls_obter_item_mensalidade ( ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(4000);
ds_lista_w			varchar(255);
pos_virgula_w			integer;
ie_acao_w			varchar(5);


BEGIN

	begin

	ds_lista_w	:= ie_tipo_item_p || ',';

	while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop
		begin
		
		select  position(',' in ds_lista_w)
		into STRICT    pos_virgula_w
		;
		
		if (pos_virgula_w > 0) then
			ie_acao_w	:= substr(ds_lista_w,1,pos_virgula_w-1);
			
			if (ie_acao_w IS NOT NULL AND ie_acao_w::text <> '') then
				ds_lista_w	:= substr(ds_lista_w,pos_virgula_w+1,length(ds_lista_w));
				
				if (coalesce(ds_retorno_w::text, '') = '') then
					ds_retorno_w	:= obter_valor_dominio(1930,ie_acao_w);
				else
					ds_retorno_w	:= ds_retorno_w || ', ' || obter_valor_dominio(1930,ie_acao_w);
				end if;
			else
				ds_lista_w	:= null;
			end if;
		end if;
		
		end;
	end loop;

	exception
	when others then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1061266, 'FIELD=Tipo Item');
	end;

return	substr(ds_retorno_w,1,4000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
