SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_item_req_executado ( nr_seq_item_aud_p bigint, ie_origem_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_origem_p
R - requisicao
G - guia
E - execucao
*/
ds_retorno_w			varchar(1) := 'N';
nr_seq_guia_w			bigint;
nr_seq_proc_origem_w		bigint;
nr_seq_mat_origem_w		bigint;
ie_status_solicitacao_w		varchar(2);
ie_origem_solic_w		pls_guia_plano.ie_origem_solic%type;


BEGIN


if (nr_seq_item_aud_p IS NOT NULL AND nr_seq_item_aud_p::text <> '') then
	begin
		select	nr_seq_proc_origem,
			nr_seq_mat_origem,
			ie_status_solicitacao
		into STRICT	nr_seq_proc_origem_w,
			nr_seq_mat_origem_w,
			ie_status_solicitacao_w
		from	pls_auditoria_item
		where	nr_sequencia = nr_seq_item_aud_p;
	exception
	when others then
		ie_status_solicitacao_w := null;
	end;
	if (ie_origem_p = 'R') then
		begin
			if (coalesce(nr_seq_proc_origem_w,0) > 0) then
				select  a.nr_seq_guia,
					(select	max(b.ie_origem_solic)
					from	pls_guia_plano b
					where	b.nr_sequencia = a.nr_seq_guia) ie_origem
				into STRICT    nr_seq_guia_w,
					ie_origem_solic_w
				from    pls_requisicao_proc a
				where   a.nr_sequencia = nr_seq_proc_origem_w;
			elsif (coalesce(nr_seq_mat_origem_w,0) > 0) then
				select  a.nr_seq_guia,
					(select	max(b.ie_origem_solic)
					from	pls_guia_plano b
					where	b.nr_sequencia = a.nr_seq_guia) ie_origem
				into STRICT    nr_seq_guia_w,
					ie_origem_solic_w
				from    pls_requisicao_mat a
				where   a.nr_sequencia = nr_seq_mat_origem_w;
			end if;
		exception
		when others then
			nr_seq_guia_w := null;
		end;
		--Verifica se existe guia gerada e se a mesma não foi gerada via WebService,  no processo da Requisição o sistema já gera a guia mesmo ficando em análise
		if ( coalesce(nr_seq_guia_w,0) > 0  and  coalesce(ie_origem_solic_w,'M') <> 'E' ) then
			ds_retorno_w := 'S';
		end if;
	elsif (ie_origem_p = 'E') then
		if (ie_status_solicitacao_w = 'A') then
			ds_retorno_w := 'S';
		end if;
	end if;
end if;

return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
