SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_itens_intercamb_lib ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(2)	:= 'S';
qt_itens_nao_lib_w		integer;
qt_proc_w			integer;
qt_mat_w			integer;
qt_itens_w			integer;


BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	select	sum(qt_item)
	into STRICT	qt_itens_w
	from (
		SELECT	count(1) qt_item
		from	pls_guia_plano_proc
		where	nr_seq_guia	= nr_seq_guia_p
		
union

		SELECT	count(1) qt_item
		from	pls_guia_plano_mat
		where	nr_seq_guia	= nr_seq_guia_p
		) alias4;

	if (qt_itens_w	> 0) then
		select	count(1)
		into STRICT	qt_proc_w
		from	pls_guia_plano_proc
		where	nr_seq_guia	= nr_seq_guia_p
		and	coalesce(nr_seq_motivo_exc::text, '') = ''
		and	ie_status	= 'U';

		select	count(1)
		into STRICT	qt_mat_w
		from	pls_guia_plano_mat
		where	nr_seq_guia	= nr_seq_guia_p
		and	coalesce(nr_seq_motivo_exc::text, '') = ''
		and	ie_status	= 'U';

		qt_itens_nao_lib_w	:= qt_proc_w + qt_mat_w;

		if (qt_itens_nao_lib_w	> 0) then
			ds_retorno_w	:= 'N';
		end if;
	else
		ds_retorno_w	:= 'N';
	end if;
elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	select	sum(qt_item)
	into STRICT	qt_itens_w
	from (
		SELECT	count(1) qt_item
		from	pls_requisicao_proc
		where	nr_seq_requisicao	= nr_seq_requisicao_p
		
union

		SELECT	count(1) qt_item
		from	pls_requisicao_mat
		where	nr_seq_requisicao	= nr_seq_requisicao_p
		) alias4;

	if (qt_itens_w	> 0) then
		select	count(1)
		into STRICT	qt_proc_w
		from	pls_requisicao_proc
		where	nr_seq_requisicao	= nr_seq_requisicao_p
		and	ie_status		= 'U';

		select	count(1)
		into STRICT	qt_mat_w
		from	pls_requisicao_mat
		where	nr_seq_requisicao	= nr_seq_requisicao_p
		and	ie_status		= 'U';

		qt_itens_nao_lib_w	:= qt_proc_w + qt_mat_w;

		if (qt_itens_nao_lib_w	> 0) then
			ds_retorno_w	:= 'N';
		end if;
	else
		ds_retorno_w	:= 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
