CREATE OR REPLACE FUNCTION pls_obter_itens_mens_lanc_prog ( nr_seq_lanc_mens_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_mensalidade_item_w	bigint;
nr_seq_item_mens_w		bigint;
ds_retorno_w			varchar(255)	:= null;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_mensalidade_seg_item	a,
		pls_mensalidade_segurado	b,
		pls_mensalidade			c
	where	a.nr_seq_mensalidade_seg	= b.nr_sequencia
	and	b.nr_seq_mensalidade		= c.nr_sequencia
	and	coalesce(c.ie_cancelamento::text, '') = ''
	and	a.nr_seq_lancamento_mens	= nr_seq_lanc_mens_p;


BEGIN

select	nr_seq_mensalidade_item
into STRICT	nr_seq_mensalidade_item_w
from	pls_lancamento_mensalidade
where	nr_sequencia	= nr_seq_lanc_mens_p;

if (coalesce(nr_seq_mensalidade_item_w,0) <> 0) then
	ds_retorno_w	:= nr_seq_mensalidade_item_w;
else
	open C01;
	loop
	fetch C01 into
		nr_seq_item_mens_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (coalesce(length(ds_retorno_w),0) <= 245) then
			if (coalesce(ds_retorno_w::text, '') = '') then
				ds_retorno_w	:= ds_retorno_w || nr_seq_item_mens_w;
			else
				ds_retorno_w	:= ds_retorno_w || ', ' || nr_seq_item_mens_w;
			end if;
		end if;
		end;
	end loop;
	close C01;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
