CREATE OR REPLACE FUNCTION pls_obter_juros_multa_aprop ( nr_titulo_p bigint, dt_referencia_p timestamp, ie_juros_multa_p text) RETURNS bigint AS $body$
DECLARE


vl_juros_w			double precision;
vl_multa_w			double precision;
cd_estabelecimento_w		smallint;


BEGIN
select	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	titulo_receber
where	nr_titulo	= nr_titulo_p;

if (ie_juros_multa_p = 'J') then
	vl_juros_w := obter_juros_multa_titulo(nr_titulo_p,dt_referencia_p,'R',ie_juros_multa_p);
	return	coalesce(vl_juros_w, 0);
elsif (ie_juros_multa_p = 'M') then
	vl_multa_w := obter_juros_multa_titulo(nr_titulo_p,dt_referencia_p,'R',ie_juros_multa_p);
	return	coalesce(vl_multa_w, 0);
elsif (ie_juros_multa_p = 'A') then
	vl_juros_w := obter_juros_multa_titulo(nr_titulo_p,dt_referencia_p,'R',ie_juros_multa_p);
	vl_multa_w := obter_juros_multa_titulo(nr_titulo_p,dt_referencia_p,'R',ie_juros_multa_p);
	return	coalesce(vl_juros_w, 0) + coalesce(vl_multa_w, 0);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
