CREATE OR REPLACE FUNCTION pls_obter_juros_multa_boleto ( nr_titulo_p bigint, dt_referencia_p timestamp, ie_pagar_receber_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


tx_retorno_w		double precision;
ie_tipo_taxa_w		varchar(1);
cd_tipo_taxa_juro_w	bigint;
cd_tipo_taxa_multa_w	bigint;


BEGIN
select	max(cd_tipo_taxa_juro),
	max(cd_tipo_taxa_multa)
into STRICT	cd_tipo_taxa_juro_w,
	cd_tipo_taxa_multa_w
from	titulo_receber
where	nr_titulo = nr_titulo_p;

if (ie_opcao_p = 'JD') then -- Juros diario
	select	max(ie_tipo_taxa)
	into STRICT	ie_tipo_taxa_w
	from	tipo_taxa
	where	cd_tipo_taxa = cd_tipo_taxa_juro_w;

	if (ie_tipo_taxa_w = 'D') then
		select	max(tx_juros)
		into STRICT	tx_retorno_w
		from	titulo_receber
		where	nr_titulo = nr_titulo_p;
	end if;

elsif (ie_opcao_p = 'MF') then -- Multa fixa
	select	max(ie_tipo_taxa)
	into STRICT	ie_tipo_taxa_w
	from	tipo_taxa
	where	cd_tipo_taxa = cd_tipo_taxa_multa_w;

	if (ie_tipo_taxa_w = 'A') then
		select	max(tx_multa)
		into STRICT	tx_retorno_w
		from	titulo_receber
		where	nr_titulo = nr_titulo_p;
	end if;
end if;

return	coalesce(tx_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
