SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_libera_alt_lib_atend ( nr_seq_regra_lib_req_web_p bigint) RETURNS varchar AS $body$
DECLARE


qt_ordem_servico_w		integer := 0;
qt_carteira_provisoria_w	integer := 0;
nr_seq_segurado_w		bigint;
ds_retorno_w			varchar(1) := 'S';


BEGIN

select	count(*)
into STRICT	qt_ordem_servico_w
from	man_ordem_servico
where	nr_seq_lib_atend_pls = nr_seq_regra_lib_req_web_p;


select 	nr_seq_segurado
into STRICT	nr_seq_segurado_w
from	pls_regra_lib_req_web
where	nr_sequencia = nr_seq_regra_lib_req_web_p;

if (coalesce(nr_seq_segurado_w,0) > 0) then

	select	count(1)
	into STRICT	qt_carteira_provisoria_w
	from	pls_segurado_carteira
	where	nr_seq_segurado	= nr_seq_segurado_w
	and	ie_situacao = 'P'
	and	clock_timestamp()	<= coalesce(dt_validade_carteira,clock_timestamp());
end if;

if (qt_ordem_servico_w > 0) or (qt_carteira_provisoria_w > 0) then
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
