CREATE OR REPLACE FUNCTION pls_obter_lib_bio_med ( nr_seq_usuario_web_p pls_usuario_web.nr_sequencia%type, cd_medico_p pessoa_fisica.cd_pessoa_fisica%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


nr_seq_regra_lib_w	pls_regra_lib_bio_exec.nr_sequencia%type;
ds_retorno_w		varchar(1)  := 'N';


BEGIN

if (pls_obter_se_controle_estab('RE') = 'S') then	
	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_lib_w
	from	pls_regra_lib_bio_exec
	where   ie_situacao	= 'A'
	and	((coalesce(cd_medico::text, '') = '')      	or (cd_medico      		= cd_medico_p))
	and	((coalesce(nr_seq_usuario_web::text, '') = '')  or (nr_seq_usuario_web    	= nr_seq_usuario_web_p))
	and	establishment_timezone_utils.startOfDay(clock_timestamp()) between establishment_timezone_utils.startOfDay(dt_inicio_vigencia) and establishment_timezone_utils.startOfDay(coalesce(dt_fim_vigencia,clock_timestamp()))
	and	cd_estabelecimento	= cd_estabelecimento_p;
else	
	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_lib_w
	from	pls_regra_lib_bio_exec
	where   ie_situacao	= 'A'
	and	((coalesce(cd_medico::text, '') = '')      	or (cd_medico      		= cd_medico_p))
	and	((coalesce(nr_seq_usuario_web::text, '') = '')  or (nr_seq_usuario_web    	= nr_seq_usuario_web_p))
	and	establishment_timezone_utils.startOfDay(clock_timestamp()) between establishment_timezone_utils.startOfDay(dt_inicio_vigencia) and establishment_timezone_utils.startOfDay(coalesce(dt_fim_vigencia,clock_timestamp()));
end if;

if (nr_seq_regra_lib_w IS NOT NULL AND nr_seq_regra_lib_w::text <> '') then
	ds_retorno_w :=  'S';
end if;
	
return  ds_retorno_w;

end;
