SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_lib_mat_med_web ( nr_seq_usu_prest_p bigint, cd_guia_p text) RETURNS varchar AS $body$
DECLARE


qt_registros_w		bigint;
qt_reg_conta_w		bigint := 0;
qt_reg_conta_compl_w	bigint := 0;
ie_retorno_w		varchar(1) := 'N';
nr_seq_guia_w		bigint;
nr_seq_prestador_w	bigint;
ie_retorno_reg_bloq_w	varchar(10);

/*
ie_retorno_w =
	'N' : Não encontrada
	'C'  : Já apresentada
	'R'  : Regra de bloqueio
	'L'  : OK
*/
BEGIN

if (cd_guia_p IS NOT NULL AND cd_guia_p::text <> '') then
	ie_retorno_reg_bloq_w := pls_obter_regra_bloq_opme(cd_guia_p);

	if (ie_retorno_reg_bloq_w = '0') then
		select  max(nr_sequencia)
		into STRICT	nr_seq_guia_w
		from	pls_guia_plano
		where   cd_guia = cd_guia_p
		and 	ie_status = 1;

		if (nr_seq_guia_w IS NOT NULL AND nr_seq_guia_w::text <> '') then
			select  count(1)
			into STRICT	qt_reg_conta_w
			from 	pls_conta
			where 	cd_guia = cd_guia_p
			and	ie_status <> 'C';

			if (qt_reg_conta_w <> 0) then
				select  count(1)
				into STRICT	qt_reg_conta_compl_w
				from	pls_conta a,
					pls_protocolo_conta b
				where   a.nr_seq_protocolo = b.nr_sequencia
				and	a.cd_guia = cd_guia_p
				and	b.ie_situacao = 'I';
				-- não pode solicitar mad/med para uma guia que já está apresentada
				if (qt_reg_conta_w <> 0 and (qt_reg_conta_w <> qt_reg_conta_compl_w)) then
					ie_retorno_w := 'C';
				end if;
			end if;

			if (ie_retorno_w = 'N') then
				--verifica se o usuário prestador, tem o prestador da guia
				select	max(nr_seq_prestador)
				into STRICT	nr_seq_prestador_w
				from	pls_guia_plano
				where	nr_sequencia = nr_seq_guia_w;

				select	count(1)
				into STRICT	qt_registros_w
				from    pls_prestador a,
					pls_prestador_usuario_web b
				where   a.ie_situacao 		= 'A'
				and     b.ie_situacao 		= 'A'
				and     a.nr_sequencia 		= b.nr_seq_prestador
				and     b.nr_seq_usuario 	= nr_seq_usu_prest_p
				and	a.nr_sequencia 		= nr_seq_prestador_w;

				if (qt_registros_w > 0) then
					ie_retorno_w := 'L';
				else
					--caso tenha regra de exeção para o usuário prestador
					select  count(1)
					into STRICT	qt_registros_w
					from	pls_permissao_sol_mat_med a,
						pls_perm_prest_mat_med b
					where	b.nr_seq_perm_mat_med = a.nr_sequencia
					and	a.nr_seq_usuario_web = nr_seq_usu_prest_p
					and	b.nr_seq_prestador = nr_seq_prestador_w
					and 	a.ie_situacao  = 'A';

					if (qt_registros_w > 0) then
						ie_retorno_w := 'L';
					end if;
				end if;
			end if;
		end if;
	else
		ie_retorno_w := 'R';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
