CREATE OR REPLACE FUNCTION pls_obter_link_nfs_mens ( nr_titulo_p titulo_receber.nr_titulo%type) RETURNS varchar AS $body$
DECLARE


cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
ds_link_w			pls_regra_link_nfs_mens.ds_link%type;
nr_seq_regra_link_nfs_w		pls_regra_link_nfs_mens.nr_sequencia%type;


BEGIN

select	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	titulo_receber
where	nr_titulo = nr_titulo_p;

select	max(nr_sequencia)
into STRICT	nr_seq_regra_link_nfs_w
from	pls_regra_link_nfs_mens
where	cd_estabelecimento = cd_estabelecimento_w;

if (nr_seq_regra_link_nfs_w IS NOT NULL AND nr_seq_regra_link_nfs_w::text <> '') then
	select	ds_link
	into STRICT	ds_link_w
	from	pls_regra_link_nfs_mens
	where	nr_sequencia = nr_seq_regra_link_nfs_w;
end if;

return	ds_link_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
