CREATE OR REPLACE FUNCTION pls_obter_link ( ie_opcao_p text, nr_seq_contrato_p text, cd_operadora_empresa_p text, cd_matricula_familia_p text, cd_usuario_plano_p pls_segurado_carteira.cd_usuario_plano%type) RETURNS varchar AS $body$
DECLARE
	

/*
ie_opcao_p
B = Benefici√°rio
C = Contrato
*/
			
ds_retorno_w	varchar(255);


BEGIN

if (ie_opcao_p = 'C') then
	select	max(ds_link)
	into STRICT	ds_retorno_w
	from	pls_contrato_ged
	where	nr_seq_contrato		= nr_seq_contrato_p
	and	ie_tipo_registro	= 'C'
	and	((cd_operadora_empresa	= cd_operadora_empresa_p or coalesce(cd_operadora_empresa_p::text, '') = '') or coalesce(cd_operadora_empresa::text, '') = '');
elsif (ie_opcao_p = 'B') then
	select	max(ds_link)
	into STRICT	ds_retorno_w
	from	pls_contrato_ged
	where	nr_seq_contrato 	= nr_seq_contrato_p
	and	ie_tipo_registro	= 'B'
	and	((cd_matricula_familia	= cd_matricula_familia_p or coalesce(cd_matricula_familia_p::text, '') = '') or coalesce(cd_matricula_familia::text, '') = '')
	and	((cd_usuario_plano = cd_usuario_plano_p or coalesce(cd_usuario_plano_p::text, '') = '') or (coalesce(cd_usuario_plano::text, '') = ''));
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
