CREATE OR REPLACE FUNCTION pls_obter_lista_atrib_end_ref ( cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


ds_lista_atributos_retorno_w	varchar(4000)	:= null;
qt_atributo_w			bigint	:= 0;
qt_regra_w			bigint;

c01 CURSOR FOR
	SELECT	a.nm_atributo
	from	pls_regra_alt_pf_ref_atrib a,
		pls_regra_alt_pf_ref b
	where	b.nr_sequencia = a.nr_seq_regra
	and	b.cd_estabelecimento = cd_estabelecimento_p
	and	b.ie_acao in ('A','U');
	
BEGIN

select	count(1)
into STRICT	qt_regra_w
from	pls_regra_alt_pf_ref_atrib a,
	pls_regra_alt_pf_ref b
where	b.nr_sequencia = a.nr_seq_regra
and	b.cd_estabelecimento = cd_estabelecimento_p
and	b.ie_acao in ('A','U');

if (qt_regra_w > 0) then
	for r_c01_w in c01 loop
		begin
		
		qt_atributo_w	:= qt_atributo_w + 1;
		
		if (qt_atributo_w = 1) then
			ds_lista_atributos_retorno_w	:= r_c01_w.nm_atributo;
		else
			ds_lista_atributos_retorno_w	:= ds_lista_atributos_retorno_w || ';' || r_c01_w.nm_atributo;
		end if;
		end;
	end loop;

	return	ds_lista_atributos_retorno_w;
else
	return 	'CD_CEP;CD_EMPRESA_REFER;CD_MUNICIPIO_IBGE;CD_PROFISSAO;CD_TIPO_LOGRADOURO;'||
		'CD_ZONA_PROCEDENCIA;DS_BAIRRO;DS_COMPLEMENTO;DS_COMPL_END;DS_EMAIL;'||
		'DS_ENDERECO;DS_FAX;DS_FONE_ADIC;DS_FONETICA;DS_MUNICIPIO;'||
		'DS_OBSERVACAO;DS_WEBSITE;IE_FATOR_RH;IE_MALA_DIRETA;IE_NF_CORREIO;'||
		'IE_OBRIGA_EMAIL;IE_TIPO_SANGUE;NM_CONTATO;NM_CONTATO_PESQUISA;NR_DDD_FAX;'||
		'NR_DDD_FONE_ADIC;NR_DDD_TELEFONE;NR_DDI_FAX;NR_DDI_FONE_ADIC;NR_DDI_TELEFONE;'||
		'NR_ENDERECO;NR_RAMAL;NR_SEQ_IDENT_CNES;NR_SEQ_LOCAL_ATEND_MED;NR_SEQ_PAIS;'||
		'NR_SEQ_PARENTESCO;NR_TELEFONE;SG_ESTADO';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
