CREATE OR REPLACE FUNCTION pls_obter_lista_solic_analise (nr_seq_analise_p bigint) RETURNS varchar AS $body$
DECLARE


ds_solicitacao_w	varchar(2000);
ds_solicitacao_ww	varchar(2000);

C01 CURSOR FOR
	SELECT	ds_tipo_solicitacao
	from	pls_analise_tipo_solic		b,
		pls_analise_solicitacao		a
	where	a.nr_seq_analise	= nr_seq_analise_p
	and	a.nr_seq_tipo_solic	= b.nr_sequencia
	and	(a.dt_conclusao IS NOT NULL AND a.dt_conclusao::text <> '');


BEGIN
open C01;
loop
fetch C01 into
	ds_solicitacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_solicitacao_ww := ds_solicitacao_w||', '||ds_solicitacao_ww;
	ds_solicitacao_w	:= '';
	end;
end loop;
close C01;

return substr(ds_solicitacao_ww,1,length(ds_solicitacao_ww)-2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
