CREATE OR REPLACE FUNCTION pls_obter_longitude_pj ( cd_cgc_p pessoa_juridica.cd_cgc%TYPE ) RETURNS varchar AS $body$
DECLARE

    ds_return_w pessoa_juridica.ds_longitude%TYPE;

BEGIN
    BEGIN
        IF (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') THEN
            SELECT MAX(pj.ds_longitude)
              INTO STRICT ds_return_w
              FROM pessoa_juridica pj
             WHERE pj.cd_cgc = cd_cgc_p;
        END IF;
    EXCEPTION WHEN no_data_found THEN NULL;
    END;

    RETURN ds_return_w;
END;
$body$
