CREATE OR REPLACE FUNCTION pls_obter_lote_conta_benef (nr_seq_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_mensalidade_w		bigint;
nr_seq_mensalidade_seg_w	bigint;
nr_seq_lote_w			bigint;



BEGIN

select 	max(nr_seq_mensalidade_seg)
into STRICT	nr_seq_mensalidade_seg_w
from   	pls_conta_coparticipacao
where	nr_seq_conta = nr_seq_conta_p;

if (nr_seq_mensalidade_seg_w > 0) then

	select 	max(nr_seq_mensalidade)
	into STRICT	nr_seq_mensalidade_w
	from 	pls_mensalidade_segurado
	where 	nr_sequencia = nr_seq_mensalidade_seg_w;


	select 	max(nr_seq_lote)
	into STRICT	nr_seq_lote_w
	from	pls_mensalidade
	where	nr_sequencia = nr_seq_mensalidade_w;

end if;


return coalesce(nr_seq_lote_w,0);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
