CREATE OR REPLACE FUNCTION pls_obter_lote_pagamento ( nr_seq_lote_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

/*
ie_opcao_p	= S -> Status;
	= VL -> valor do lote
	= DTC -> Data mÃªs competencia
	= DTP -> Data periodo
*/
ds_retorno_w			varchar(255);


BEGIN
if (ie_opcao_p	= 'S') then
	select	substr(obter_valor_dominio( 3471, ie_status),1,255)
	into STRICT	ds_retorno_w
	from	pls_lote_pagamento
	where	nr_sequencia	= nr_seq_lote_p;
end if;

if (ie_opcao_p	= 'VL') then
	select	max(vl_lote)
	into STRICT	ds_retorno_w
	from	pls_lote_pagamento
	where	nr_sequencia	= nr_seq_lote_p;
end if;

if (ie_opcao_p	= 'DTC') then
	select	substr(pls_obter_mes_competencia( nr_seq_competencia, cd_estabelecimento_p),1,70)
	into STRICT	ds_retorno_w
	from	pls_lote_pagamento
	where	nr_sequencia	= nr_seq_lote_p;
end if;

if (ie_opcao_p	= 'DTP') then
	select	substr(pls_obter_desc_periodo(nr_seq_periodo),1,255)
	into STRICT	ds_retorno_w
	from	pls_lote_pagamento
	where	nr_sequencia	= nr_seq_lote_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
