SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_macro_mensalidade ( nr_sequencial_p bigint, nm_atributo_p text, ds_macro_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(255);
ds_forma_cobranca_w		varchar(255);
ds_agencia_estab_w		varchar(255);
vl_resultado_w			double precision;
nr_seq_nota_fiscal_w		bigint;
dt_referencia_w			timestamp;
ds_observacao_w			varchar(255);
nm_fantasia_plano_w		pls_plano.nm_fantasia%type;
ds_item_mens_w			varchar(255);
i				bigint := 0;
nr_seq_contrato_w		pls_contrato.nr_sequencia%type;
nr_seq_pagador_intercambio_w	pls_intercambio.nr_sequencia%type;
dt_mesano_referencia_w		pls_lote_mensalidade.dt_mesano_referencia%type;
dt_contrato_w			timestamp;
dt_ano_w			varchar(255);
qt_vidas_w			bigint;
nr_seq_pagador_w		pls_contrato_pagador.nr_sequencia%type;

C01 CURSOR FOR 
	SELECT	b.nm_fantasia 
	from	pls_mensalidade_segurado a, 
		pls_plano b 
	where	a.nr_seq_plano = b.nr_sequencia 
	and	a.nr_seq_mensalidade = nr_sequencial_p 
	group by b.nm_fantasia;

C02 CURSOR FOR 
	SELECT	obter_valor_dominio(1930,a.ie_tipo_item) ds_item_mens 
	from	pls_mensalidade_seg_item a, 
		pls_mensalidade_segurado b 
	where	a.nr_seq_mensalidade_seg = b.nr_sequencia 
	and	b.nr_seq_mensalidade = nr_sequencial_p 
	group by a.ie_tipo_item 
	order by a.ie_tipo_item;


BEGIN 
 
if (UPPER(nm_atributo_p) = UPPER('NR_SEQ_MENSALIDADE')) then 
	if (UPPER(ds_macro_p) in (UPPER('@AGENCIA_ESTAB'),UPPER('@FORMA_COBRANCA'))) then 
		select	forma_cobranca, 
			substr(ds_conta,1,255) 
		into STRICT	ds_forma_cobranca_w, 
			ds_agencia_estab_w 
		from	pls_mensalidade_wcp_v 
		where	nr_sequencia	= nr_sequencial_p;
		 
		if (UPPER(ds_macro_p) = UPPER('@AGENCIA_ESTAB')) then 
			ds_retorno_w	:= ds_agencia_estab_w;
		elsif (UPPER(ds_macro_p) = UPPER('@FORMA_COBRANCA')) then 
			ds_retorno_w	:= ds_forma_cobranca_w;
		end if;
	elsif (UPPER(ds_macro_p) = UPPER('@DT_REFERENCIA_MENS')) then 
		select	a.dt_mesano_referencia 
		into STRICT	dt_referencia_w 
		from	pls_mensalidade		b, 
			pls_lote_mensalidade	a 
		where	b.nr_seq_lote		= a.nr_sequencia 
		and	b.nr_sequencia		= nr_sequencial_p;
		 
		ds_retorno_w	:= to_char(dt_referencia_w,'yyyy/mm');
	elsif (UPPER(ds_macro_p) = UPPER('@REF_MENS_DDMMYYYY')) then 
		select	a.dt_mesano_referencia 
		into STRICT	dt_referencia_w 
		from	pls_mensalidade		b, 
			pls_lote_mensalidade	a 
		where	b.nr_seq_lote		= a.nr_sequencia 
		and	b.nr_sequencia		= nr_sequencial_p;
		 
		ds_retorno_w	:= to_char(dt_referencia_w,'dd/mm/yyyy');
	elsif (UPPER(ds_macro_p) = UPPER('@REF_MENS_1_DDMMYYYY')) then 
		select	add_months(a.dt_mesano_referencia,1) 
		into STRICT	dt_referencia_w 
		from	pls_mensalidade		b, 
			pls_lote_mensalidade	a 
		where	b.nr_seq_lote		= a.nr_sequencia 
		and	b.nr_sequencia		= nr_sequencial_p;
		 
		ds_retorno_w	:= to_char(dt_referencia_w,'dd/mm/yyyy');
	elsif (UPPER(ds_macro_p) = UPPER('@NR_CONTRATO')) then 
		select	max(a.nr_contrato) 
		into STRICT	ds_retorno_w 
		from	pls_mensalidade		c, 
			pls_contrato_pagador	b, 
			pls_contrato		a 
		where	c.nr_seq_pagador	= b.nr_sequencia 
		and	b.nr_seq_contrato	= a.nr_sequencia 
		and	c.nr_sequencia		= nr_sequencial_p;
	elsif (UPPER(ds_macro_p) = UPPER('@VL_ATO_PRINCIPAL')) then 
		select	sum(c.vl_ato_cooperado) 
		into STRICT	vl_resultado_w 
		from	pls_mensalidade_seg_item	c, 
			pls_mensalidade_segurado	b, 
			pls_mensalidade			a 
		where	c.nr_seq_mensalidade_seg	= b.nr_sequencia 
		and	b.nr_seq_mensalidade		= a.nr_sequencia 
		and	a.nr_sequencia			= nr_sequencial_p;
		 
		ds_retorno_w	:= campo_mascara_virgula(vl_resultado_w);
	elsif (UPPER(ds_macro_p) = UPPER('@VL_ATO_AUXILIAR')) then 
		select	sum(c.vl_ato_auxiliar) 
		into STRICT	vl_resultado_w 
		from	pls_mensalidade_seg_item	c, 
			pls_mensalidade_segurado	b, 
			pls_mensalidade			a 
		where	c.nr_seq_mensalidade_seg	= b.nr_sequencia 
		and	b.nr_seq_mensalidade		= a.nr_sequencia 
		and	a.nr_sequencia			= nr_sequencial_p;
		 
		ds_retorno_w	:= campo_mascara_virgula(vl_resultado_w);
	elsif (UPPER(ds_macro_p) = UPPER('@VL_ATO_NAO_COOP')) then 
		select	sum(c.vl_ato_nao_cooperado) 
		into STRICT	vl_resultado_w 
		from	pls_mensalidade_seg_item	c, 
			pls_mensalidade_segurado	b, 
			pls_mensalidade			a 
		where	c.nr_seq_mensalidade_seg	= b.nr_sequencia 
		and	b.nr_seq_mensalidade		= a.nr_sequencia 
		and	a.nr_sequencia			= nr_sequencial_p;
		 
		ds_retorno_w	:= campo_mascara_virgula(vl_resultado_w);
	elsif (UPPER(ds_macro_p) = UPPER('@VL_BASE_IR')) then 
		select	sum(c.vl_ato_cooperado) 
		into STRICT	vl_resultado_w 
		from	pls_mensalidade_seg_item	c, 
			pls_mensalidade_segurado	b, 
			pls_mensalidade			a 
		where	c.nr_seq_mensalidade_seg	= b.nr_sequencia 
		and	b.nr_seq_mensalidade		= a.nr_sequencia 
		and	a.nr_sequencia			= nr_sequencial_p;
		 
		ds_retorno_w	:= campo_mascara_virgula(vl_resultado_w);
	elsif (UPPER(ds_macro_p) = UPPER('@VL_IR')) then 
		select	max(nr_sequencia_nf) 
		into STRICT	nr_seq_nota_fiscal_w 
		from	pls_mensalidade_wcp_v 
		where	nr_sequencia	= nr_sequencial_p;
		 
		vl_resultado_w	:=	obter_dados_nota_fiscal(nr_seq_nota_fiscal_w,7);
		 
		ds_retorno_w	:= campo_mascara_virgula(vl_resultado_w);
	elsif (UPPER(ds_macro_p) = UPPER('@NM_FANTASIA_PLANO')) then 
		i	:= 0;
		open C01;
		loop 
		fetch C01 into 
			nm_fantasia_plano_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin 
			i	:= i + 1;
			 
			if (i > 1) then 
				ds_retorno_w	:= ds_retorno_w || chr(13);
			end if;
			 
			ds_retorno_w	:= ds_retorno_w || nm_fantasia_plano_w;
			end;
		end loop;
		close C01;
	elsif (UPPER(ds_macro_p) = UPPER('@ITENS_MENSALIDADE')) then 
		i	:= 0;
		open C02;
		loop 
		fetch C02 into 
			ds_item_mens_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin 
			i	:= i + 1;
			 
			if (i > 1) then 
				ds_retorno_w	:= ds_retorno_w || chr(13);
			end if;
			 
			ds_retorno_w	:= ds_retorno_w || ds_item_mens_w;
			end;
		end loop;
		close C02;
	elsif (UPPER(ds_macro_p) = UPPER('@DT_PORTAB')) or (UPPER(ds_macro_p) = UPPER('@DT_FIM_PORTAB')) then 
		 
		select	max(b.nr_seq_pagador_intercambio), 
			max(b.nr_seq_contrato), 
			max(d.dt_mesano_referencia) 
		into STRICT	nr_seq_pagador_intercambio_w, 
			nr_seq_contrato_w, 
			dt_mesano_referencia_w 
		from	pls_mensalidade		a, 
			pls_contrato_pagador	b, 
			pls_lote_mensalidade	d 
		where	a.nr_seq_lote		= d.nr_sequencia 
		and	a.nr_seq_pagador	= b.nr_sequencia 
		and	a.nr_sequencia		= nr_sequencial_p;
		 
		if (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then 
			select	max(dt_contrato) 
			into STRICT	dt_contrato_w 
			from	pls_contrato 
			where	nr_sequencia = nr_seq_contrato_w;
		elsif (nr_seq_pagador_intercambio_w IS NOT NULL AND nr_seq_pagador_intercambio_w::text <> '') then 
			select	max(dt_inclusao) 
			into STRICT	dt_contrato_w 
			from	pls_intercambio 
			where	nr_sequencia = nr_seq_pagador_intercambio_w;
		end if;
		 
		if (dt_contrato_w IS NOT NULL AND dt_contrato_w::text <> '') and (dt_mesano_referencia_w IS NOT NULL AND dt_mesano_referencia_w::text <> '') then 
			select	CASE WHEN to_char(dt_contrato_w,'mm')='01' THEN to_char(add_months(dt_mesano_referencia_w,12),'/yyyy')  ELSE to_char(dt_mesano_referencia_w,'/yyyy') END  
			into STRICT	dt_ano_w 
			;
 
			ds_retorno_w	:= '01/'||to_char(dt_contrato_w,'mm')||dt_ano_w;
		else 
			ds_retorno_w	:= null;
		end if;
 
		if (UPPER(ds_macro_p) = UPPER('@DT_FIM_PORTAB')) then 
			ds_retorno_w	:= fim_mes(add_months(ds_retorno_w,3));
		end if;
	elsif (upper(ds_macro_p) = upper('@QT_VIDAS_CONTRATO')) then 
		select	a.dt_referencia, 
			b.nr_seq_contrato 
		into STRICT	dt_referencia_w, 
			nr_seq_contrato_w 
		from	pls_mensalidade a, 
			pls_contrato_pagador b 
		where	a.nr_seq_pagador = b.nr_sequencia 
		and	a.nr_sequencia = nr_sequencial_p;
		 
		select	count(1) 
		into STRICT	qt_vidas_w 
		from	pls_segurado 
		where	nr_seq_contrato = nr_seq_contrato_w 
		and	trunc(dt_contratacao,'mm') <= trunc(dt_referencia_w,'mm') 
		and	((coalesce(dt_rescisao::text, '') = '') or (dt_rescisao >= dt_referencia_w)) 
		and	coalesce(dt_cancelamento::text, '') = '' 
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
		 
		ds_retorno_w	:= qt_vidas_w;
	elsif (upper(ds_macro_p) = upper('@QT_VIDAS_PAGADOR')) then 
		select	dt_referencia, 
			nr_seq_pagador 
		into STRICT	dt_referencia_w, 
			nr_seq_pagador_w 
		from	pls_mensalidade 
		where	nr_sequencia = nr_sequencial_p;
		 
		select	count(1) 
		into STRICT	qt_vidas_w 
		from	pls_segurado 
		where	nr_seq_pagador = nr_seq_pagador_w 
		and	trunc(dt_contratacao,'mm') <= trunc(dt_referencia_w,'mm') 
		and	((coalesce(dt_rescisao::text, '') = '') or (dt_rescisao >= dt_referencia_w)) 
		and	coalesce(dt_cancelamento::text, '') = '' 
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
		 
		ds_retorno_w	:= qt_vidas_w;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_macro_mensalidade ( nr_sequencial_p bigint, nm_atributo_p text, ds_macro_p text) FROM PUBLIC;

