CREATE OR REPLACE FUNCTION pls_obter_mascara_arq_adic ( nr_seq_fatura_p pls_fatura.nr_sequencia%type, nm_mascara_arq_adic_p pls_regra_arquivo_fatura.nm_mascara_arquivo_adic%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) RETURNS varchar AS $body$
DECLARE

					
ds_retorno_w			varchar(255);
nr_fatura_w			ptu_fatura.nr_fatura%type;
nr_titulo_w			pls_fatura.nr_titulo%type;
qt_caracter_w			integer;
ds_caracter_w			varchar(255);
ds_valor_w			varchar(255);


BEGIN

if (nr_seq_fatura_p IS NOT NULL AND nr_seq_fatura_p::text <> '') and (nm_mascara_arq_adic_p IS NOT NULL AND nm_mascara_arq_adic_p::text <> '') then
	ds_retorno_w 	:= nm_mascara_arq_adic_p;
	
	select	max(nr_fatura)
	into STRICT	nr_fatura_w
	from	ptu_fatura
	where	nr_seq_pls_fatura = nr_seq_fatura_p;
	
	select	max(b.nr_titulo)
	into STRICT	nr_titulo_w
	from	pls_fatura b,
		pls_lote_faturamento	a
	where	a.nr_sequencia		= b.nr_seq_lote
	and	b.nr_sequencia		= nr_seq_fatura_p;
	
	nr_fatura_w := coalesce(nr_fatura_w, to_char(nr_titulo_w));
	
	if (nm_mascara_arq_adic_p like '%F%') then
		qt_caracter_w	:= length(nm_mascara_arq_adic_p) - length(replace(nm_mascara_arq_adic_p, 'F', ''));
		ds_caracter_w	:= lpad('F', qt_caracter_w, 'F');
		
		ds_valor_w	:= substr(coalesce(nr_fatura_w, 0), 1, qt_caracter_w);
		ds_valor_w	:= lpad(ds_valor_w, qt_caracter_w, '0');
		
		ds_retorno_w	:= replace(ds_retorno_w, ds_caracter_w, ds_valor_w);
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
