CREATE OR REPLACE FUNCTION pls_obter_mascara_cartao_benef ( nr_seq_emissor_p text, ie_opcao_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


/*
E - Conforme o emissor do parâmetro
O - Conforme o emissor padrão da gestão de operadoras
*/
ds_retorno_w		varchar(255);
nr_seq_emissor_w	bigint;


BEGIN

if (ie_opcao_p = 'E') then
	select	max(ds_mascara)
	into STRICT	ds_retorno_w
	from	pls_regra_carteira
	where 	nr_sequencia	= (	SELECT	max(nr_sequencia)
					from	pls_regra_carteira
					where	nr_seq_emissor	= nr_seq_emissor_p
					and 	ie_situacao	= 'A');
elsif (ie_opcao_p = 'O') then
	select	max(nr_seq_emissor)
	into STRICT	nr_seq_emissor_w
	from	pls_parametros
	where	cd_estabelecimento	= cd_estabelecimento_p;

	select	max(ds_mascara)
	into STRICT	ds_retorno_w
	from	pls_regra_carteira
	where 	nr_sequencia	=	(SELECT	max(nr_sequencia)
					from	pls_regra_carteira
					where	nr_seq_emissor	= nr_seq_emissor_w
					and	ie_tipo_estipulante = 'PF'
					and 	ie_situacao	= 'A');

	if (coalesce(ds_retorno_w::text, '') = '') then
		select	max(ds_mascara)
		into STRICT	ds_retorno_w
		from	pls_regra_carteira
		where 	nr_sequencia	=	(SELECT	max(nr_sequencia)
						from	pls_regra_carteira
						where	nr_seq_emissor	= nr_seq_emissor_w
						and	ie_tipo_estipulante = 'A'
						and 	ie_situacao	= 'A');
	end if;

	if (coalesce(ds_retorno_w::text, '') = '') then
		select	max(ds_mascara)
		into STRICT	ds_retorno_w
		from	pls_regra_carteira
		where 	nr_sequencia	=	(SELECT	max(nr_sequencia)
						from	pls_regra_carteira
						where	nr_seq_emissor	= nr_seq_emissor_w
						and	ie_tipo_estipulante = 'PJ'
						and 	ie_situacao	= 'A');
	end if;
end if;

ds_retorno_w	:= replace(ds_retorno_w,'0','9');

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
