CREATE OR REPLACE FUNCTION pls_obter_max_desconto_tabela ( nr_seq_tabela_p bigint, nm_usuario_p usuario.nm_usuario%type, ie_tipo_estipulante_p text default null) RETURNS bigint AS $body$
DECLARE

				 
nr_seq_proposta_w	pls_tabela_preco.nr_seq_proposta%type;
nr_contrato_w		pls_tabela_preco.nr_contrato%type;
tx_desconto_w		pls_regra_desc_man_preco.tx_desconto%type;
tx_retorno_w		bigint;
ie_tipo_estipulante_w	varchar(2);

C01 CURSOR FOR 
	SELECT	tx_desconto 
	from 	pls_regra_desc_man_preco 
	where 	coalesce(nm_usuario_liberacao,nm_usuario_p) = nm_usuario_p 
	and 	coalesce(cd_perfil,obter_perfil_ativo) = obter_perfil_ativo 
	and (ie_tipo_estipulante = 'A' 
	or 	ie_tipo_estipulante = ie_tipo_estipulante_w) 
	order by 
		nm_usuario, 
		cd_perfil;
		

BEGIN 
 
if (ie_tipo_estipulante_p IS NOT NULL AND ie_tipo_estipulante_p::text <> '') then 
	ie_tipo_estipulante_w := ie_tipo_estipulante_p;
else 
	select	max(nr_seq_proposta), 
		max(nr_contrato) 
	into STRICT	nr_seq_proposta_w, 
		nr_contrato_w 
	from 	pls_tabela_preco a 
	where 	nr_sequencia = nr_seq_tabela_p;
 
	if (nr_contrato_w IS NOT NULL AND nr_contrato_w::text <> '') then 
		ie_tipo_estipulante_w := pls_obter_dados_contrato(nr_contrato_w, 'TE');
	elsif (nr_seq_proposta_w IS NOT NULL AND nr_seq_proposta_w::text <> '') then 
		ie_tipo_estipulante_w := pls_obter_dados_proposta(nr_seq_proposta_w, 'TE');
	end if;
end if;
 
open C01;
loop 
fetch C01 into	 
	tx_desconto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	tx_retorno_w := tx_desconto_w;
	end;
end loop;
close C01;
 
return tx_retorno_w;
 
end;
$body$
