CREATE OR REPLACE FUNCTION pls_obter_max_protocolo ( nr_seq_prestador_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_protocolo_w	bigint;


BEGIN

select  coalesce(max(nr_sequencia), 0)
into STRICT	nr_seq_protocolo_w
from    pls_protocolo_conta
where   ie_status = 1
and     nr_seq_prestador = nr_seq_prestador_p
and     ie_situacao  = 'DW'
and     trunc(dt_mes_competencia, 'month') = trunc(clock_timestamp(), 'month');

return	nr_seq_protocolo_w;
end 	;
