CREATE OR REPLACE FUNCTION pls_obter_medico_partic_dif_oc ( nr_seq_proc_p bigint, cd_medico_p bigint, nr_seq_grau_partic_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_gerar_ocorrencia_w	varchar(1) := 'N';
nr_seq_proc_ref_w	bigint;
qt_duplicado_w		integer;


BEGIN

select	max(nr_seq_proc_ref)
into STRICT	nr_seq_proc_ref_w
from	pls_conta_proc
where	nr_sequencia = nr_seq_proc_p;

if (coalesce(nr_seq_proc_ref_w::text, '') = '') then
	nr_seq_proc_ref_w	:= nr_seq_proc_p;
end if;

select	count(1)
into STRICT	qt_duplicado_w

where	exists	(SELECT	'S'
		from	pls_conta_proc a,
			pls_conta c
		where	c.nr_sequencia		= a.nr_seq_conta
		and	((a.nr_seq_proc_ref 	= nr_seq_proc_ref_w) or ((a.nr_sequencia = nr_seq_proc_ref_w) and (coalesce(a.nr_seq_proc_ref::text, '') = '')))
		and	c.cd_medico_executor	= cd_medico_p
		and	c.nr_seq_grau_partic	<> nr_seq_grau_partic_p
		
union

		SELECT	'S'
		from	pls_conta_proc a,
			pls_proc_participante b
		where	b.nr_seq_conta_proc	= a.nr_sequencia
		and	((a.nr_seq_proc_ref 	= nr_seq_proc_ref_w) or ((a.nr_sequencia = nr_seq_proc_ref_w) and (coalesce(a.nr_seq_proc_ref::text, '') = '')))
		and	b.cd_medico		= cd_medico_p
		and	b.nr_seq_grau_partic	<> nr_seq_grau_partic_p);

if (qt_duplicado_w > 0) then
	ie_gerar_ocorrencia_w	:= 'S';
end if;

return	ie_gerar_ocorrencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
