SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_medico_principal ( nr_seq_conta_proc_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
	C - Cód médico
	E - Especialidade
	S - Nr seq cbo saúde
	G - Grau participação
	T - Cód TISS
*/
ds_retorno_w		varchar(255);
nr_seq_proc_partic_w	bigint := 0;
nr_seq_conta_w		bigint;
cd_guia_w		varchar(20);
cd_guia_referencia_w	varchar(20);
nr_seq_conta_ww		bigint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
cd_medico_w		varchar(10);
nr_seq_cbo_saude_w	bigint;
ds_cbo_w		varchar(255);
nr_seq_grau_partic_w	bigint;
cd_tiss_w		varchar(20);

C01 CURSOR FOR
	SELECT	b.nr_sequencia
	from	pls_conta_proc a,
		pls_proc_participante b,
		pls_grau_participacao c
	where	a.nr_sequencia		= b.nr_seq_conta_proc
	and	b.nr_seq_grau_partic	= c.nr_sequencia
	and	a.cd_procedimento	= cd_procedimento_w
	and	a.ie_origem_proced	= ie_origem_proced_w
	and	a.nr_seq_conta		= nr_seq_conta_w
	order by (c.cd_tiss)::numeric  desc;


BEGIN

select	nr_seq_conta,
	cd_procedimento,
	ie_origem_proced
into STRICT	nr_seq_conta_w,
	cd_procedimento_w,
	ie_origem_proced_w
from	pls_conta_proc
where	nr_sequencia	= nr_seq_conta_proc_p;

select	coalesce(max(cd_guia),'0'),
	coalesce(max(cd_guia_referencia),'0')
into STRICT	cd_guia_w,
	cd_guia_referencia_w
from	pls_conta
where	nr_sequencia	= nr_seq_conta_w;

if (cd_guia_referencia_w <> '0') and (cd_guia_referencia_w <> cd_guia_w) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_conta_ww
	from	pls_conta
	where	cd_guia	= cd_guia_referencia_w;

	if (coalesce(nr_seq_conta_ww,0) <> 0) then
		nr_seq_conta_w	:= nr_seq_conta_ww;
	end if;
end if;

open C01;
loop
fetch C01 into
	nr_seq_proc_partic_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if (coalesce(nr_seq_proc_partic_w,0) <> 0) then
	select	a.cd_medico,
		a.nr_seq_cbo_saude,
		b.ds_cbo,
		a.nr_seq_grau_partic
	into STRICT	cd_medico_w,
		nr_seq_cbo_saude_w,
		ds_cbo_w,
		nr_seq_grau_partic_w
	FROM pls_proc_participante a
LEFT OUTER JOIN cbo_saude b ON (a.nr_seq_cbo_saude = b.nr_sequencia)
WHERE a.nr_sequencia	= nr_seq_proc_partic_w;
end if;

if (ie_opcao_p	= 'C') then
	ds_retorno_w := cd_medico_w;
elsif (ie_opcao_p	= 'E') then
	ds_retorno_w := ds_cbo_w;
elsif (ie_opcao_p	= 'S') then
	ds_retorno_w := to_char(nr_seq_cbo_saude_w);
elsif (ie_opcao_p	= 'G') then
	ds_retorno_w := to_char(nr_seq_grau_partic_w);
elsif (ie_opcao_p	= 'T') then
	select	max(cd_tiss)
	into STRICT	cd_tiss_w
	from	pls_grau_participacao
	where	nr_sequencia	= nr_seq_grau_partic_w;

	ds_retorno_w := cd_tiss_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
